import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";


const AkunTable = () => {
    const [akun, setAkun] = useState([]);

    useEffect(() => {
        getAkun();
    }, []);

    const getAkun = async () => {
        const response = await axios.get("http://localhost:5000/akun");
        setAkun(response.data);
    };

    const deleteAkun = async (id_akun) => {
        try {
            await axios.delete(`http://localhost:5000/akun/${id_akun}`);
            getAkun();
        } catch (error) {
            console.log(error);
        }
    };

    return (
        <div className="container mt-5">
            <Link to="/add" className="btn btn-success">
                Akun Table
            </Link>
            <div className="py-4">
                <table className="table table-hover">
                    <thead className="table-primary">
                        <tr >
                            <th scope="col">No</th>
                            <th scope="col">Nama AKUN</th>
                            <th scope="col">STATUS</th>
                            <th scope="col">Action</th>
                        </tr>
                    </thead>
                    <tbody>

                        {akun.map((akun, i) => (

                            <tr>
                                <td>{i + 1}</td>
                                <td>{akun.nama_akun}</td>
                                <td>{akun.status}</td>
                                <td>
                                    <div className="d-grid gap-2 justify-content-md-start">
                                        <Link to={`edit/${akun.id_akun}`} className="card-footer-item">
                                            Edit
                                        </Link>
                                        <a
                                            onClick={() => deleteAkun(akun.id_akun)}
                                            className="card-footer-item"
                                        >
                                            Delete
                                        </a>

                                    </div>
                                </td>
                            </tr>
                        ))}

                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default AkunTable;
