import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";


const AkunTable = () => {
    const [akun, setAkun] = useState([]);

    useEffect(() => {
        getAkun();
    }, []);

    const getAkun = async () => {
        const response = await axios.get("http://localhost:5000/akun");
        setAkun(response.data);
    };

    const deleteAkun = async (id_akun) => {
        try {
            await axios.delete(`http://localhost:5000/akun/${id_akun}`);
            getAkun();
        } catch (error) {
            console.log(error);
        }
    };

    return (
        <div className="container mt-5">
            <Link to="/add" className="btn btn-success">
                Akun Table
            </Link>
            <div className="py-4">
                <div className="columns is-multiline mt-2">
                    <table className="table table-hover">
                        <thead className="table-primary">
                            <tr>
                                <th scope="col">No</th>
                                <th scope="col">Nama AKUN</th>
                                <th scope="col">STATUS</th>
                                <th scope="col">Action</th>
                            </tr>
                        </thead>
                        <tbody>

                            {akun.map((akun, i) => (

                                <tr>
                                    <td>{i + 1}</td>
                                    <td>{akun.nama_akun}</td>
                                    <td>{akun.status}</td>
                                    <td>
                                        <div className="d-grid gap-2 d-md-flex justify-content-md-center">
                                            <button className="btn btn-warning me-md-2" type="button">Button</button>
                                            <button className="btn btn-danger" type="button">Button</button>
                                        </div>
                                    </td>
                                </tr>
                            ))}

                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    );
};

export default AkunTable;
