import { BrowserRouter, Routes, Route } from "react-router-dom";
import AkunList from "./components/akun/AkunList";
import AddAkun from "./components/akun/AddAkun";
import EditAkun from "./components/akun/EditAkun";

const expressLayouts = require('express-ejs-layouts');

//menggunakan ejs
app.set('view engine', 'ejs');
app.use(expressLayouts);
app.use(express.static('public')); //agar bisa akses folder gambar dll
app.use(express.urlencoded({
  extended: true
}));

function App() {

  app.get('/', async (req, res) => {
    const var_menu = await menu.find();

    res.render('home', {
      layout: 'main-layout',
      title: 'Home',
      var_menu,
      msg: req.flash('msg'),
    });

  });
  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<AkunList />} />
        <Route path="add" element={<AddAkun />} />
        <Route path="edit/:id" element={<EditAkun />} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
